mapper.update:

update ACCOUNT set 
	BALANCE = BALANCE + #{amount}
where ACCOUNT_NO = #{accountNo}


update ACCOUNT set 
	BALANCE = BALANCE - #{amount}
where ACCOUNT_NO = #{accountNo}  
and BALANCE - #{amount} >= 0


update ACCOUNT set 
	FROZEN = FROZEN + #{amount}
where ACCOUNT_NO = #{accountNo}


update ACCOUNT set 
	FROZEN = FROZEN - #{amount}
where ACCOUNT_NO = #{accountNo}  
and FROZEN - #{amount} >= 0
